        SELECT

        FROM TNT_ORDER o


                AND TNT_ORDER_ID = #{tntOrderId}


                AND ORDER_ID = #{orderId}


                AND DISTRIBUTOR_ID = #{distributorId}


                   AND (create_Operator = #{createOperator}
                       or not exists   (select 1
			           from tnt_sub_user su, tnt_user tu
			          where su.tnt_user_id = tu.user_id
			            and su.tnt_user_id = o.distributor_id
			            and (o.create_operator = su.sub_name or
			                o.create_operator = tu.user_name)))

