o.*,DOWNPAYMENT_STATUS,d.DOWNPAYMENT_AMOUNT DOWNPAYMENT_AMOUNT_,FINALPAYMENT_STATUS,FINALPAYMENT_AMOUNT,FINALPAYMENT_PAYTARGET,REMARK,FINALPAYMENT_PAYTIME
